exports.registerCommand = function ( program, logger, retrieveConfig ) {
  program
    .command( "custom" )
    .action( function( opts ){
      var retrieveConfigOpts = {
        buildFirst: true
      };
      retrieveConfig( retrieveConfigOpts, function( mimosaConfig ) {
        logger.success("THIS IS AFTER THE BUILD");
        process.exit(0)
      });
    });
};
